def.body:
var = @scope.lookup(expr.name)
value = evaluate(expr.value.as(Expression::Base))
case expr.operator.type
when Syntax::PlusEqual
  if var.is_a?(Float)
    if value.is_a?(Float)
      @scope.assign(expr.name, var + value)
    else
      if value.is_a?(Int)
        @scope.assign(expr.name, var + value.to_f)
      else
        report_error("Invalid '+' operand type", value.class.to_s, expr.operator)
      end
    end
  else
    if var.is_a?(Int)
      if value.is_a?(Int)
        @scope.assign(expr.name, var + value)
      else
        if value.is_a?(Float)
          @scope.assign(expr.name, var.to_f + value)
        else
          report_error("Invalid '+' operand type", value.class.to_s, expr.operator)
        end
      end
    else
      if var.is_a?(String)
        if value.is_a?(String)
          @scope.assign(expr.name, var + value)
        else
          report_error("Invalid '+' operand type", value.class.to_s, expr.operator)
        end
      else
        report_error("Invalid '+' operand type", var.class.to_s, expr.operator)
      end
    end
  end
when Syntax::MinusEqual
  if var.is_a?(Float)
    if value.is_a?(Float)
      @scope.assign(expr.name, var + value)
    else
      if value.is_a?(Int)
        @scope.assign(expr.name, var + value.to_f)
      else
        report_error("Invalid '+' operand type", value.class.to_s, expr.operator)
      end
    end
  else
    if var.is_a?(Int)
      if value.is_a?(Int)
        @scope.assign(expr.name, var + value)
      else
        if value.is_a?(Float)
          @scope.assign(expr.name, var.to_f + value)
        else
          report_error("Invalid '+' operand type", value.class.to_s, expr.operator)
        end
      end
    else
      report_error("Invalid '+' operand type", var.class.to_s, expr.operator)
    end
  end
when Syntax::StarEqual
  if var.is_a?(Float)
    if value.is_a?(Float)
      @scope.assign(expr.name, var * value)
    else
      if value.is_a?(Int)
        @scope.assign(expr.name, var * value.to_f)
      else
        report_error("Invalid '*' operand type", value.class.to_s, expr.operator)
      end
    end
  else
    if var.is_a?(Int)
      if value.is_a?(Int)
        @scope.assign(expr.name, var * value)
      else
        if value.is_a?(Float)
          @scope.assign(expr.name, var.to_f * value)
        else
          report_error("Invalid '*' operand type", value.class.to_s, expr.operator)
        end
      end
    else
      report_error("Invalid '*' operand type", var.class.to_s, expr.operator)
    end
  end
when Syntax::SlashEqual
  if var.is_a?(Float)
    if value.is_a?(Float)
      @scope.assign(expr.name, var / value)
    else
      if value.is_a?(Int)
        @scope.assign(expr.name, var / value.to_f)
      else
        report_error("Invalid '/' operand type", value.class.to_s, expr.operator)
      end
    end
  else
    if var.is_a?(Int)
      if value.is_a?(Int)
        @scope.assign(expr.name, var / value)
      else
        if value.is_a?(Float)
          @scope.assign(expr.name, var.to_f / value)
        else
          report_error("Invalid '/' operand type", value.class.to_s, expr.operator)
        end
      end
    else
      report_error("Invalid '/' operand type", var.class.to_s, expr.operator)
    end
  end
when Syntax::PercentEqual
  if var.is_a?(Float)
    if value.is_a?(Float)
      @scope.assign(expr.name, var % value)
    else
      if value.is_a?(Int)
        @scope.assign(expr.name, var % value.to_f)
      else
        report_error("Invalid '%' operand type", value.class.to_s, expr.operator)
      end
    end
  else
    if var.is_a?(Int)
      if value.is_a?(Int)
        @scope.assign(expr.name, var % value)
      else
        if value.is_a?(Float)
          @scope.assign(expr.name, var.to_f % value)
        else
          report_error("Invalid '%' operand type", value.class.to_s, expr.operator)
        end
      end
    else
      report_error("Invalid '%' operand type", var.class.to_s, expr.operator)
    end
  end
when Syntax::CaratEqual
  if var.is_a?(Float)
    if value.is_a?(Float)
      @scope.assign(expr.name, var ** value)
    else
      if value.is_a?(Int)
        @scope.assign(expr.name, var ** value.to_f)
      else
        report_error("Invalid '^' operand type", value.class.to_s, expr.operator)
      end
    end
  else
    if var.is_a?(Int)
      if value.is_a?(Int)
        @scope.assign(expr.name, var ** value)
      else
        if value.is_a?(Float)
          @scope.assign(expr.name, var.to_f ** value)
        else
          report_error("Invalid '^' operand type", value.class.to_s, expr.operator)
        end
      end
    else
      report_error("Invalid '^' operand type", var.class.to_s, expr.operator)
    end
  end
end

fn = @scope.lookup(expr.var.token)
if fn.is_a?(Function) || fn.is_a?(IntrinsicFunction)
else
  report_error("Attempt to call", TypeChecker.get_mapped(fn.class), expr.var.token)
end
if fn.arity.includes?(expr.arguments.size)
else
  report_error("Expected #{fn.arity} arguments, got", expr.arguments.size.to_s, expr.var.token)
end
if fn.is_a?(IntrinsicFunction)
else
  @meta["block_return_type"] = fn.definition.return_typedef.value.to_s
end
arg_values = expr.arguments.map do |arg|
  evaluate(arg.as(Expression::Base))
end
fn.call(arg_values)

typedef = Token.new(Syntax::TypeDef, "fn", Location.new(@file_path, 0, 0))
fn = Function.new(self, @scope, stmt)
@scope.declare(typedef, stmt.identifier, fn)
fn

if @variables.has_key?(identifier.value.to_s)
  typedef, old_value = @variables[identifier.value.to_s]
  TypeChecker.assert(typedef, value, identifier)
  @variables[identifier.value.to_s] = {typedef, value}
  return value
end
if @parent.nil?
else
  return @parent.not_nil!.assign(identifier, value)
end
Logger.report_error("Undefined variable", identifier.value.to_s, identifier)

if value == nil
else
  TypeChecker.assert(typedef.value.to_s, value, typedef)
end
@variables[identifier.value.to_s] = {typedef.value.to_s, value}
value
