def.body:
((((("VarDeclaration<
" + "  #{TAB * indent}typedef: #{@typedef.value},
") + "  #{TAB * indent}var: #{@var.token.value.to_s},
") + "  #{TAB * indent}value: #{@value.to_s(indent + 1)}
") + "  #{TAB * indent}constant?: #{@constant}
") + "  #{TAB * indent}visibility: #{@visibility.to_s}
") + "#{TAB * indent}>"

((((((("FunctionDef<
" + "  #{TAB * indent}identifier: #{@identifier.value.to_s},
") + "  #{TAB * indent}parameters: [
") + "    #{TAB * indent}#{@parameters.map() do |__arg0|
  (__arg0.to_s(indent + 2)).as(String)
end.join(",
#{TAB * (indent + 2)}")}
") + "  #{TAB * indent}],
") + "  #{TAB * indent}return_typedef: #{@return_typedef.value},
") + "  #{TAB * indent}body: #{@body.to_s(indent + 1)}
") + "  #{TAB * indent}visibility: #{@visibility.to_s}
") + "#{TAB * indent}>"

fn = Function.new(self, @scope, stmt)
typedef = Token.new("func", Syntax::TypeDef, "func", Location.new(@file_path, 0, 0))
@scope.declare(typedef, stmt.identifier, fn, const: true, visibility: stmt.visibility)

var = @interpreter.scope.lookup(expr.name)
value = @interpreter.evaluate(expr.value)
op = "/="
if var.is_a?(Float)
  if value.is_a?(Float)
    return @interpreter.scope.assign(expr.name, var / value)
  end
  if value.is_a?(Int)
    return @interpreter.scope.assign(expr.name, var / value.to_f)
  end
  Logger.report_error("Invalid '#{op}' operand type", value.class.to_s, expr.operator)
else
  if var.is_a?(Int)
    if value.is_a?(Int)
      return @interpreter.scope.assign(expr.name, var / value)
    end
    if value.is_a?(Float)
      return @interpreter.scope.assign(expr.name, var.to_f / value)
    end
    Logger.report_error("Invalid '#{op}' operand type", value.class.to_s, expr.operator)
  end
end
Logger.report_error("Invalid '#{op}' operand type", var.class.to_s, expr.operator)

name_token = expr.is_a?(Expression::CompoundAssignment) ? expr.name : expr.operand.token
var = @interpreter.scope.lookup(name_token)
value = expr.is_a?(Expression::CompoundAssignment) ? @interpreter.evaluate(expr.value) : 1
if var.is_a?(Float)
  if value.is_a?(Float)
    return @interpreter.scope.assign(name_token, var - value)
  end
  if value.is_a?(Int)
    return @interpreter.scope.assign(name_token, var - value.to_f)
  end
  Logger.report_error("Invalid '#{op}' operand type", value.class.to_s, expr.operator)
else
  if var.is_a?(Int)
    if value.is_a?(Int)
      return @interpreter.scope.assign(name_token, var - value)
    end
    if value.is_a?(Float)
      return @interpreter.scope.assign(name_token, var.to_f - value)
    end
    Logger.report_error("Invalid '#{op}' operand type", value.class.to_s, expr.operator)
  end
end
Logger.report_error("Invalid '#{op}' operand type", var.class.to_s, expr.operator)

var = @interpreter.scope.lookup(expr.name)
value = @interpreter.evaluate(expr.value)
op = "%="
if var.is_a?(Float)
  if value.is_a?(Float)
    return @interpreter.scope.assign(expr.name, var % value)
  end
  if value.is_a?(Int)
    return @interpreter.scope.assign(expr.name, var % value.to_f)
  end
  Logger.report_error("Invalid '#{op}' operand type", value.class.to_s, expr.operator)
